INSERT INTO client_details (client_description,reuse_refresh_tokens,allow_introspection,client_id,client_secret,access_token_validity_seconds,refresh_token_validity_seconds,client_name,logo_uri,policy_uri,created_at,clear_access_tokens_on_refresh,code_challenge_method,device_code_validity_seconds,modified_at,pending_approval,approved,requester,reviewer)
VALUES (${client_description},${reuse_refresh_tokens},${allow_introspection},${client_id},${client_secret},${access_token_validity_seconds},${refresh_token_validity_seconds},${client_name},${logo_uri},${policy_uri},to_timestamp(${created_at}/1000.0),${clear_access_tokens_on_refresh},${code_challenge_method},${device_code_validity_seconds},to_timestamp(${modified_at}/1000.0),${pending_approval},${approved},${requester},${reviewer})
RETURNING *
